import jsonimport boto3import os# Initialize SQS clientsqs = boto3.client('sqs')# Retrieve environment variablesQUEUE_URL = os.environ['QUEUE_URL']def lambda_handler(event, context):    booking_request = {        'customerId': event['customerId'],        'roomId': event['roomId'],        'startDate': event['startDate'],        'endDate': event['endDate']    }    response = sqs.send_message(        QueueUrl=QUEUE_URL,        MessageBody=json.dumps(booking_request)    )    return {        'statusCode': 200,        'body': json.dumps('Booking request sent to the queue.')    }