import jsonimport boto3from boto3.dynamodb.conditions import Key, Attrdef lambda_handler(event, context):    # Initialize DynamoDB client    dynamodb = boto3.resource('dynamodb')    table = dynamodb.Table('Rooms')    try:        body = json.loads(event['body'])        roomId = body['roomid']        Features = body['features']        type = body['type']        Price = body['price']        new_room = {            'roomid': roomId,            'type': type,            'features': Features,            'price': Price        }        table.put_item(Item=new_room)        return {            'statusCode': 200,            'body': json.dumps({'message': 'Room added successfully'}),            'headers': {                'Content-Type': 'application/json'            }        }    except Exception as e:        return {            'statusCode': 500,            'body': json.dumps({'error': str(e)}),            'headers': {                'Content-Type': 'application/json'            }        }